package fundamentos.variaveis;

public class OperadoresRelacionais {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a = 15, b = 12;
        System.out.println("Valores de a = " + a +" b = " + b);
        System.out.println("a == b -> " + (a == b));
        System.out.println("a != b -> " + (a != b));
        System.out.println("a < b -> " + (a < b));
        System.out.println("a > b -> " + (a > b));
        System.out.println("a <= b -> " + (a <= b));
        System.out.println("a >= b -> " + (a >= b));
	}

}
